
const readlineSync = require('readline-sync');
const greeting = readlineSync.question(' Welcome to the Brain Games!Answer "yes" if the number is even, otherwise answer "no". May I have your name?');
console.log('Hello, ' + greeting + '!'); 

const num = () => {
  return Math.ceil(Math.random() * 100);
};//получаем случайное целое число 


const yesNo = rep => {
  if (rep % 2 === 0) {
    return "yes";
  }

  return "no";
};//получаем 'no' если нечетное, 'yes' если четноe

const even = (i, r) => {
  console.log(`${"Question:"}${r}`);
  let repla = r;
  const set1 = readlineSync.question("Your answer:");

  if (i <= 0) {
    return `${"Congratulations,"} ${greeting}!`;
  }

  if (yesNo(r) === set1) {
    console.log("Correct!");
    return even(i - 1, num());
  }

  return `'${set1} ${'is wrong answer ;(. Correct answer was'} '${yesNo(repla)}'.
	${"Let's try again,"} ${greeting}`;
};

console.log(even(2, num()));
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

export default even;
